

## Install APT Packages
sudo apt update
sudo apt upgrade -y
sudo apt install -y \
    apt-transport-https \
    ca-certificates \
    curl \
    gnupg \
    lsb-release \
    hardinfo \
    vim \
    git \
    screen \
    wget \
    apt-transport-https \
    software-properties-common
sudo apt autoremove -y

sudo cp bin/* /usr/local/bin

## Install Convenient Dotfiles
git submodule init
git submodule update
cp -r ./dots/.[^.]* ${HOME}


# Sensormatic Logo

echo "$SLOGO" | base64 --decode | sudo tee /usr/share/backgrounds/custom.png > /dev/null
sudo chmod 644 /usr/share/backgrounds/custom.png
gsettings set org.gnome.desktop.background picture-uri 'file:///usr/share/backgrounds/custom.png'
gsettings set org.gnome.desktop.background picture-options 'centered'
gsettings set org.gnome.desktop.background primary-color '#333333'
gsettings set org.gnome.login-screen logo '/usr/share/backgrounds/custom.png'
gsettings set org.gnome.login-screen fallback-logo '/usr/share/backgrounds/custom.png'
gsettings set org.gnome.login-screen banner-message-text 'mlcoe'
